const z = {
  object: (shape) => ({
    parse: (data) => {
      const parsed = {};
      for (const key in shape) {
        parsed[key] = shape[key].parse(data[key]);
      }
      return parsed;
    },
  }),
  string: () => ({
    min: (len) => ({
      parse: (value) => {
        if (typeof value !== 'string' || value.length < len) {
          throw new Error('Invalid string');
        }
        return value;
      },
    }),
    parse: (value) => {
      if (typeof value !== 'string') throw new Error('Invalid string');
      return value;
    },
  }),
};
module.exports = { z };
